<?phpclass L_app extends Controller {function L_app(){parent::Controller();$this->common->is_login(1);}		function index(){	$this->load->helper(array('form', 'url'));				$this->load->library('form_validation');		$this->load->model('welcome_mod');			$this->form_validation->set_rules('asking', 'Asking ammount', 'trim|required|min_length[4]|max_length[12]|xss_clean');$this->form_validation->set_rules('nomini', 'Nomini name', 'trim|required|xss_clean');//$this->form_validation->set_rules('passconf', 'Password Confirmation', 'trim|required');if ($this->form_validation->run() == FALSE)		{			$this->load->view('l_appview');		}		else		{				 $query= $this->db->get_where('loan_app', array('num' => $this->session->userdata('num')));                if($query->num_rows() > 0)				{					$data['error']='You have already applied';					$this->load->view('l_appview',$data);					//exit(0);                }								else{					$this->welcome_mod->l_app();			$data['ur']='l_app';			$this->load->view('fformsuccess',$data);			}		}	}	}	?>