<?phpclass M_collection extends Controller {function M_collection(){parent::Controller();$this->common->is_elogin(1);}		function index()	{					$this->load->helper(array('form', 'url'));				$this->load->library('form_validation');		$this->load->model('welcome_mod');			$this->form_validation->set_rules('username', 'Number', 'trim|required|min_length[5]|max_length[12]|xss_clean');$this->form_validation->set_rules('fullname', 'Fullname', 'trim|required');$this->form_validation->set_rules('mnumber', 'Month number', 'trim|required');$this->form_validation->set_rules('mrate', 'Monthly rate', 'trim|required');$this->form_validation->set_rules('due', 'Due', 'trim|required');$this->form_validation->set_rules('fine', 'Fine', 'trim|required');$this->form_validation->set_rules('book', 'Book', 'trim|required');$this->form_validation->set_rules('tdeposit', 'Total deposit', 'trim|required');$this->form_validation->set_rules('tpay', 'Total payment', 'trim|required');if ($this->form_validation->run() == FALSE)		{			$this->load->view('m_collection');		}		else		{			$this->welcome_mod->mcollection();			$data['ur']='m_collection';			$this->load->view('eformsuccess',$data);		}	}		}	?>